mapper.select:
select

from repayment_plan
where loan_id = #{loanId,jdbcType=VARCHAR}
  and plan_no = #{planNo,jdbcType=INTEGER}

mapper.delete:
delete from repayment_plan
where loan_id = #{loanId,jdbcType=VARCHAR}
  and plan_no = #{planNo,jdbcType=INTEGER}

mapper.update:
update repayment_plan
set payable_date = #{payableDate,jdbcType=TIMESTAMP},
  payable_amount = #{payableAmount,jdbcType=DECIMAL},
  payable_interest = #{payableInterest,jdbcType=DECIMAL},
  payable_capital = #{payableCapital,jdbcType=DECIMAL},
  status = #{status,jdbcType=VARCHAR}
where loan_id = #{loanId,jdbcType=VARCHAR}
  and plan_no = #{planNo,jdbcType=INTEGER}
